%span_shared:hover {
    background: $secondary-color;
    color: $primary-color;
}

a {
    text-decoration: none;
}

// nav bar styling for mobile
.nav {
    position: absolute;
    top: 3rem;
    left: -30rem;
    background: $primary-color;
    width: 18rem;
    height: 100vh;
    transition: left 1s ease-out;
    z-index: 200;
}

.open_sidebar {
    left: 0;
}

.nav_container {
    //    height: 100vh;
    display: flex;
    flex-direction: column;
}

// sign in and log in button the side bar
.user_button {
    width: 100%;
    height: 7rem;
    display: flex;
    align-items: center;
    justify-content: space-around;
}

.log_in {
    @extend %span_shared;
    background: $sign-up-color;
    color: $primary-color;

}

.sign_up {
    background: $log-in-color;
    color: $secondary-color;
    @extend %span_shared
}

//search bar in the sidebar styling
.top__items {
    height: 10rem;
}

.search__bar {
    width: 100%;
    height: 2.5rem;
}

.search_body {
    display: inline-block;
    height: 2rem;
    width: 70%;
    margin-left: 0.7rem;
}

.search_body input {
    display: inline-block;
    height: 100%;
    width: 100%;
    border: none;
    background: $secondary-color;
    border-radius: 0.2rem;
    text-indent: 1rem;


}

.search_body input::placeholder {
    text-transform: capitalize;
    font-size: 0.9rem;
}

.search__button {
    background: $log-in-color;
    padding: 0.5rem;
    cursor: pointer;
    border-radius: 0.2rem;
    color: $secondary-color;

}



// order list, list and links in the sider bar
//.links__container {
//    background: red;
//    // margin-top: 1.4rem;
//}

.links__container li {
    display: inline-block;
    // background: blue;
    height: 2.5rem;
    border-bottom: 1px solid $primary-color;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0.6rem 0;
    cursor: pointer;
}

.links__container li>a {
    display: inline-block;
    font-size: 1.5rem;
    font-weight: lighter;
    text-transform: capitalize;
    margin-left: 0.9rem;
    color: $secondary-color;

}

// open and close dropdown effects
.open_drop_down {
    padding: 0.5rem;
    margin-right: 0.3rem;
}

.fa-chevron-right {
    color: $secondary-color;
    transition: transform 0.1s ease-out;
}

.fa-chevron-right .open {
    transform: rotate(90deg);
}

// social media styling in side bar
.social_media_container {
    //    background: chocolate;
    height: 5rem;
    display: flex;
    align-items: center;
    justify-content: center;
}

.social__icons {
    margin: 0 1rem;
    padding: 0.2rem;
    cursor: pointer;
}

%social_icon_share {
    font-size: 2rem;
    color: $secondary-color;
}

#facebook {
    @extend %social_icon_share;
    color: #3b5998;
}

#twitter {
    @extend %social_icon_share;
    color: royalblue;

}

#instagram {
    @extend %social_icon_share;
    color: #e95950;

}

#whatsapp {
    @extend %social_icon_share;
    color: lime;


}

