.drop_down {
    position: absolute;
    top: 0;
    left: -19rem;
    width: 100%;
    background: $secondary-color;
    transition: left 1s ease-in-out;

    .open_left {
        width: 100%;
        height: 3rem;
        display: flex;
        flex-direction: row-reverse;
        justify-content: space-around;
        align-items: center;
        background: $primary-color;
        cursor: pointer;
    }

    .fa-chevron-left {
        margin-left: 0.4rem;
        font-size: 1.5rem;
        color: $secondary-color;
    }

    .open_left p {
        font-size: 1.4rem;
        font-weight: lighter;
        text-transform: uppercase;
        margin-right: 0.9rem;
        color: $secondary-color;

    }


    ul>li>a {
        display: inline-block;
        font-size: 1.5rem;
        margin-left: 2rem;
        color: $primary-color
    }

}

.active {
    left: 0;
}

