        @extend %signup_button;
        cursor: pointer;
        background: $log-in-color;
        color: $secondary-color;
        box-shadow: 1px 2px 2px $log-in-color;

      }

      #signup_login:focus {
        outline: 2px solid $sign-up-color;
      }
