// footer section styling
$primary-color: #2a0845;
$secondary-color: #f1f1f1;
$sign-up-color: #4caf50;
$log-in-color: #ff0000;
$main_color: #ccc;

footer {
    height: 22rem;
    width: 100%;
    background: $main_color;
}

.newsletter {
    width: 100%;
    max-width: 64rem;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    margin: 0 auto;
}

.newsletter::placeholder {
    text-align: center;
}

%header_six {
    font-size: 1.1rem;
    text-transform: capitalize;
    text-align: center;
    font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
}

.email_container {
    height: 5rem;
    width: 18rem;
    margin-top: 0.6rem;

    h4 {
        font-size: 1.4rem;
        text-transform: capitalize;
        font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
        text-align: center;
        margin-bottom: 0.3rem;
    }

    h6 {
        @extend %header_six;
        margin-bottom: 0.3rem;
    }

    .email {
        width: 100%;
        height: 2.3rem;
        margin: 0.7rem auto;
        overflow: hidden;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    input {
        padding: 0.5rem;
        border: none;
        border-radius: 0.1rem;
        text-indent: 1rem;
        margin-right: 0.1rem;
    }

    input:focus {
        outline-color: $primary-color;
    }

    .arrow {
        top: 0;
        display: inline-block;
        padding: 0.5rem;
        background: $primary-color;
        cursor: pointer;
    }

    .fa-chevron-right {
        font-size: 0.7rem;
    }
}

.socialmedia_container {
    height: 6rem;
    width: 18rem;
    display: flex;
    justify-content: center;
    align-items: center;

    .footer__icon {
        margin: 1rem;
    }
}

.about_footer {
    width: 16rem;
    height: 4rem;
    // background: darkblue;

    h1 {
        font-size: 1.1rem;
        text-transform: capitalize;
        text-align: center;
    }

    h6 {
        @extend %header_six;
        margin: 0.4rem 0;
    }

    a {
        display: block;
        @extend %header_six;
    }
}

.line {
    height: 0.01rem;
    width: 98%;
    background: $primary-color;
    margin: 0 auto;
    margin-top: 0.6rem;
}

.developer_brand {
    height: 4rem;
    width: 10rem;
    margin: 0 auto;
    color: $primary-color;;

    .copyright {
        display: block;
        @extend %header_six;
        margin: 0.5rem auto;
    }

    .date {
        display: block;
        @extend %header_six;

    }
}

@media screen and (min-width:768px) {
    footer {
        height: 15rem;
    }

    .newsletter {
        height: 13rem;
        margin-top: 0.6rem;
        flex-direction: row;
    }

    .fa-chevron-right {
        font-size: 1rem !important;
        transform: none;
        color: $secondary-color
    }

    .line {
        margin-top: -2.5rem !important;
    }
}
